	$templateCode = str_replace('<%%UPLOADFILE(B_assignment)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(C_assignment)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(bank)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Class)%%>', safe_html($urow['Class']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Class)%%>', html_attr($row['Class']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Class)%%>', urlencode($urow['Class']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Name)%%>', safe_html($urow['Name']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Name)%%>', html_attr($row['Name']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Name)%%>', urlencode($urow['Name']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Type)%%>', safe_html($urow['Type']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Type)%%>', html_attr($row['Type']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Type)%%>', urlencode($urow['Type']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Hours)%%>', safe_html($urow['Hours']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Hours)%%>', html_attr($row['Hours']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Hours)%%>', urlencode($urow['Hours']), $templateCode);
		$templateCode = str_replace('<%%VALUE(NameTypeHours)%%>', safe_html($urow['NameTypeHours']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(NameTypeHours)%%>', urlencode($urow['NameTypeHours']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(A_assignment)%%>', safe_html($urow['A_assignment']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(A_assignment)%%>', safe_html($urow['A_assignment'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(A_assignment)%%>', urlencode($urow['A_assignment']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(B_assignment)%%>', safe_html($urow['B_assignment']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(B_assignment)%%>', safe_html($urow['B_assignment'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(B_assignment)%%>', urlencode($urow['B_assignment']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(C_assignment)%%>', safe_html($urow['C_assignment']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(C_assignment)%%>', safe_html($urow['C_assignment'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(C_assignment)%%>', urlencode($urow['C_assignment']), $templateCode);
		$templateCode = str_replace('<%%CHECKED(bank)%%>', ($row['bank'] ? "checked" : ""), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Class)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Class)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Name)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Name)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Type)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Type)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Hours)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Hours)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(NameTypeHours)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(NameTypeHours)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(A_assignment)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(A_assignment)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(B_assignment)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(B_assignment)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(C_assignment)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(C_assignment)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%CHECKED(bank)%%>', '', $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Lessons');
	if($selected_id) {
		$jdata = get_joined_record('Lessons', $selected_id);
		if($jdata === false) $jdata = get_defaults('Lessons');
		$rdata = $row;
	}
	$templateCode .= loadView('Lessons-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: Lessons_dv
	if(function_exists('Lessons_dv')) {
		$args = [];
		Lessons_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}