function Outcoming_Files_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Outcoming_Files');
	if(!$arrPerm['insert']) return false;

	$data = [
		'protocol_id' => Request::lookup('protocol_id', ''),
		'title' => Request::val('title', ''),
		'doc_template_id' => Request::lookup('doc_template_id', ''),
		'hex_doc' => br2nl(Request::val('hex_doc', '')),
		'file' => Request::fileUpload('file', [
			'maxSize' => 5120000,
			'types' => 'txt|doc|docx|docm|odt|pdf|rtf',
			'noRename' => false,
			'dir' => '',
			'success' => function($name, $selected_id) {
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!strlen(Request::val('SelectedID'))) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('Outcoming_Files', 'file', Request::val('SelectedID'));
			},
		]),
	];

	if($data['title'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} '&#920;&#941;&#956;&#945;': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: Outcoming_Files_before_insert
	if(function_exists('Outcoming_Files_before_insert')) {
		$args = [];
		if(!Outcoming_Files_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Outcoming_Files', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Outcoming_Files', $recID, calculated_fields()['Outcoming_Files']);

	// hook: Outcoming_Files_after_insert
	if(function_exists('Outcoming_Files_after_insert')) {
		$res = sql("SELECT * FROM `Outcoming_Files` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Outcoming_Files_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	// record owner is current user
	$recordOwner = getLoggedMemberID();
	set_record_owner('Outcoming_Files', $recID, $recordOwner);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Outcoming_Files_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

