}

function Outcoming_Files_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('Outcoming_Files', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: Outcoming_Files_before_delete
	if(function_exists('Outcoming_Files_before_delete')) {
		$args = [];
		if(!Outcoming_Files_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `Outcoming_Files` WHERE `id`='{$selected_id}'", $eo);

	// hook: Outcoming_Files_after_delete
	if(function_exists('Outcoming_Files_after_delete')) {
		$args = [];
		Outcoming_Files_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='Outcoming_Files' AND `pkValue`='{$selected_id}'", $eo);
}

function Outcoming_Files_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('Outcoming_Files', $selected_id, 'edit')) return false;

	$data = [
		'protocol_id' => Request::lookup('protocol_id', ''),
		'title' => Request::val('title', ''),
		'doc_template_id' => Request::lookup('doc_template_id', ''),
		'hex_doc' => br2nl(Request::val('hex_doc', '')),
		'file' => Request::fileUpload('file', [
			'maxSize' => 5120000,
			'types' => 'txt|doc|docx|docm|odt|pdf|rtf',
			'noRename' => false,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
			},
			'removeOnRequest' => true,
			'remove' => function($selected_id) {
				// do nothing: preserve removed files on server.
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('Outcoming_Files', 'file', $selected_id);
			},
		]),
	];

	if($data['title'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} '&#920;&#941;&#956;&#945;': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('Outcoming_Files', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: Outcoming_Files_before_update
	if(function_exists('Outcoming_Files_before_update')) {
		$args = ['old_data' => $old_data];
		if(!Outcoming_Files_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'Outcoming_Files', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="Outcoming_Files_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('Outcoming_Files', $data['selectedID'], calculated_fields()['Outcoming_Files']);

	// hook: Outcoming_Files_after_update
	if(function_exists('Outcoming_Files_after_update')) {
		$res = sql("SELECT * FROM `Outcoming_Files` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!Outcoming_Files_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('Outcoming_Files', $selected_id);
}

