			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		else
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		// if not in embedded mode and user has insert only but no view/update/delete,
		// remove 'back' button
		if(
			$arrPerm['insert']
			&& !$arrPerm['update'] && !$arrPerm['delete'] && !$arrPerm['view']
			&& !Request::val('Embedded')
		)
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
		elseif($separateDV)
			$templateCode = str_replace(
				'<%%DESELECT_BUTTON%%>', 
				'<button
					type="submit" 
					class="btn btn-default" 
					id="deselect" 
					name="deselect_x" 
					value="1" 
					onclick="' . $backAction . '" 
					title="' . html_attr($Translation['Back']) . '">
						<i class="glyphicon glyphicon-chevron-left"></i> ' .
						$Translation['Back'] .
				'</button>',
				$templateCode
			);
		else
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#protocol_id').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#protocol_id_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#title').replaceWith('<div class=\"form-control-static\" id=\"title\">' + (jQuery('#title').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#doc_template_id').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#doc_template_id_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#hex_doc').replaceWith('<div class=\"form-control-static\" id=\"hex_doc\">' + (jQuery('#hex_doc').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#file').replaceWith('<div class=\"form-control-static\" id=\"file\">' + (jQuery('#file').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#file, #file-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif(($AllowInsert && !$selected_id) || ($AllowUpdate && $selected_id)) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(protocol_id)%%>', $combo_protocol_id->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(protocol_id)%%>', $combo_protocol_id->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(protocol_id)%%>', urlencode($combo_protocol_id->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(doc_template_id)%%>', $combo_doc_template_id->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(doc_template_id)%%>', $combo_doc_template_id->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(doc_template_id)%%>', urlencode($combo_doc_template_id->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['protocol_id' => ['Protocol', '&#913;&#961;. &#928;&#961;&#969;&#964;&#959;&#954;&#972;&#955;&#955;&#959;&#965;'], 'doc_template_id' => ['Documents_templates', '&#904;&#947;&#947;&#961;&#945;&#966;&#959; &#945;&#960;&#972; &#960;&#961;&#972;&#964;&#965;&#960;&#959;'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(protocol_id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(title)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(doc_template_id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(hex_doc)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(file)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<input type="file" name="file" id="file" data-filetypes="txt|doc|docx|docm|odt|pdf|rtf" data-maxsize="5120000" style="max-width: calc(100% - 1.5rem);" accept=".txt,.doc,.docx,.docm,.odt,.pdf,.rtf">' . '<i class="text-danger clear-upload hidden pull-right" style="margin-top: -.1em; font-size: large;">&times;</i>'), $templateCode);
	if($AllowUpdate && $row['file'] != '') {
		$templateCode = str_replace('<%%REMOVEFILE(file)%%>', '<input type="checkbox" name="file_remove" id="file_remove" value="1"> <label for="file_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	} else {
		$templateCode = str_replace('<%%REMOVEFILE(file)%%>', '', $templateCode);
	}

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(protocol_id)%%>', safe_html($urow['protocol_id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(protocol_id)%%>', html_attr($row['protocol_id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(protocol_id)%%>', urlencode($urow['protocol_id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(title)%%>', safe_html($urow['title']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(title)%%>', html_attr($row['title']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(title)%%>', urlencode($urow['title']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(doc_template_id)%%>', safe_html($urow['doc_template_id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(doc_template_id)%%>', html_attr($row['doc_template_id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(doc_template_id)%%>', urlencode($urow['doc_template_id']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(hex_doc)%%>', safe_html($urow['hex_doc']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(hex_doc)%%>', safe_html($urow['hex_doc'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(hex_doc)%%>', urlencode($urow['hex_doc']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(file)%%>', safe_html($urow['file']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(file)%%>', html_attr($row['file']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(file)%%>', urlencode($urow['file']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(protocol_id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(protocol_id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(title)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(title)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(doc_template_id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(doc_template_id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(hex_doc)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(hex_doc)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(file)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(file)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('fileEdit')) { document.getElementById('fileEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('fileEditLink')) { document.getElementById('fileEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Outcoming_Files');
	if($selected_id) {
		$jdata = get_joined_record('Outcoming_Files', $selected_id);
		if($jdata === false) $jdata = get_defaults('Outcoming_Files');
		$rdata = $row;
	}
	$templateCode .= loadView('Outcoming_Files-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: Outcoming_Files_dv
	if(function_exists('Outcoming_Files_dv')) {
		$args = [];
		Outcoming_Files_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}