function Teachers_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Teachers');
	if(!$arrPerm['insert']) return false;

	$data = [
		'SectorID' => Request::lookup('SectorID', ''),
		'Name' => Request::val('Name', ''),
		'Name2' => Request::lookup('Name2', ''),
		'Placement' => Request::val('Placement', ''),
		'Assumption_Date' => Request::dateComponents('Assumption_Date', ''),
		'Mandatory_Hours' => Request::val('Mandatory_Hours', '0'),
		'Main_Sector' => Request::val('Main_Sector', ''),
	];


	// hook: Teachers_before_insert
	if(function_exists('Teachers_before_insert')) {
		$args = [];
		if(!Teachers_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Teachers', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Teachers', $recID, calculated_fields()['Teachers']);

	// hook: Teachers_after_insert
	if(function_exists('Teachers_after_insert')) {
		$res = sql("SELECT * FROM `Teachers` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Teachers_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	// record owner is current user
	$recordOwner = getLoggedMemberID();
	set_record_owner('Teachers', $recID, $recordOwner);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Teachers_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

