function Teachers_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('Teachers', $selected_id, 'edit')) return false;

	$data = [
		'SectorID' => Request::lookup('SectorID', ''),
		'Name' => Request::val('Name', ''),
		'Name2' => Request::lookup('Name2', ''),
		'Placement' => Request::val('Placement', ''),
		'Assumption_Date' => Request::dateComponents('Assumption_Date', ''),
		'Mandatory_Hours' => Request::val('Mandatory_Hours', ''),
		'Main_Sector' => Request::val('Main_Sector', ''),
	];

	// get existing values
	$old_data = getRecord('Teachers', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: Teachers_before_update
	if(function_exists('Teachers_before_update')) {
		$args = ['old_data' => $old_data];
		if(!Teachers_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'Teachers', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="Teachers_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('Teachers', $data['selectedID'], calculated_fields()['Teachers']);

	// hook: Teachers_after_update
	if(function_exists('Teachers_after_update')) {
		$res = sql("SELECT * FROM `Teachers` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!Teachers_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('Teachers', $selected_id);
}

